import React from "react";
import { GET_MY_PROFILE, GET_USER_BY_ID } from "../gqloperations/queries";
import { useQuery } from "@apollo/client";
import { useParams } from "react-router-dom";

export default function OtherUserProfile() {
  const { userid } = useParams();
  const { loading, data, error } = useQuery(GET_USER_BY_ID, {
    variables: {
      userid: userid,
    },
  });

  console.log("uid", userid);
  if (loading) return <h1>profile is Loading !!!</h1>;

  if (error) return <h1>{error.message}</h1>;

  console.log(data.user.quotes);
  return (
    <div className="container my-container">
      <div className="center-align">
        <img
          className="circle"
          style={{ border: "2px solid", marginTop: "10px" }}
          src={`https://robohash.org/${data.user.firstName}.png?size=200x200`}
          alt="pic"
        />
        <h5>
          {data.user.firstName} {data.user.lastName}
        </h5>
        <h6>Email - {data.user.email}</h6>
      </div>
      <h3>Your quotes</h3>
      {data.user.quotes.map((quo) => {
        return (
          <blockquote>
            <h6>{quo.name}</h6>
          </blockquote>
        );
      })}
    </div>
  );
}
